
$(document).ready(function(){var tabel_pembayaran_ota=$('#tabel_pembayaran_ota').DataTable({ajax:url+'listener_show_all_pembayaran_saya_ota',processing:true,columns:[{sName:"id"},{sName:"nama santri"},{sName:"Total Biaya"}]});$(document).on('click','.lanjut_bayar',function(){var cek=$('#tabel_pembayaran_otaku').html();$('#cek').html(cek);$('#modal_confirm').modal('show');})
$('#form_tambah').on('submit',function(e){e.preventDefault();$.ajax({type:'post',url:url+'save',data:$('#form_tambah').serialize(),dataType:'json',success:function(dt){$('#modal_confirm').modal('hide');alert("Data Invoice Berhasil Disimpan, Cek Email Untuk Melihat Detail Pembayaran");}});});$(document).on('click','.lanjut_bayar_psb',function(){var cek_psb=$('#tabel_pembayaran_psb').html();$('#cek_psb').html(cek_psb);$('#modal_confirm_psb').modal('show');})
$('#form_tambah_psb').on('submit',function(e){e.preventDefault();$.ajax({type:'post',url:url+'save_psb',data:$('#form_tambah_psb').serialize(),dataType:'json',success:function(dt){$('#modal_confirm_psb').modal('hide');alert("Data Invoice Berhasil Disimpan, Cek Email Untuk Melihat Detail Pembayaran");}});});})